import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import './home.css';
import Receitas from "../../assets/Receitas.json";

function Home() {
    const [receitas, setReceitas] = useState([]);

    setReceitas(Receitas);

    return (
        <div className="container">
            <div className="lista-receitas">
                {receitas.map((receita) => (
                    <div className="receita-card" key={receita.id} >
                        <img src={receita.imagem} alt="Imagem da receita" />
                        <h1>{receita.nome}</h1>
                        <h3>{receita.descricaoReceita}</h3>
                        <Link to={`/receita/${receita.id}`} className="btn-receita-completa">
                            RECEITA COMPLETA
                        </Link>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Home;
